definition.script:
node {
     def build_id = env.BUILD_ID
     if(!PARAMETER_PARENT_JOB_ID.equals('')) {
         build_id = PARAMETER_PARENT_JOB_ID
     }
     def output_directory = env.JENKINS_OUTPUT + '/' + env.JOB_NAME + '/' + build_id
     def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
     print(dockerfile_dir)
     dir(dockerfile_dir) {
         stage('iRODS Build') {
             def base_os = PARAMETER_PLATFORM_TARGETS.split(',')
             def parallelBranches = [:]
             for(int i = 0; i < base_os.size(); i++) {
                def os = base_os[i]
                def build_cmd = 'python build_externals_wrapper.py --platform_target ' + os + ' --build_id ' + build_id + ' --externals_repo ' + PARAMETER_EXTERNALS_GIT_REPO + ' --externals_commitish ' + PARAMETER_EXTERNALS_GIT_COMMITISH + ' --output_directory ' + output_directory
                def branchName = os.split(':')[0]
                parallelBranches["${branchName}"] = {
                    sh build_cmd 
                }
             }
             if (parallelBranches.size() != 0) {
                 parallel(parallelBranches)
             }
         }
     }
}
