definition.script:
node {
     def build_id = env.BUILD_ID
     def output_directory = env.JENKINS_OUTPUT + '/' + env.JOB_NAME + '/' + PARAMETER_IMAGE_TAG
     def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
     print(dockerfile_dir)
     dir(dockerfile_dir) {
         stage('iRODS Build') {
             def base_os = PARAMETER_PLATFORM_TARGETS.split(',')
             def parallelBranches = [:]
             for(int i = 0; i < base_os.size(); i++) {
                def os = base_os[i]
                def build_cmd = 'python build_irods.py -p ' + os +
                                ' --image_tag ' + PARAMETER_IMAGE_TAG +
                                ' -b ' + build_id + 
                                ' --irods_repo ' + PARAMETER_IRODS_REPO +
                                ' --irods_commitish ' + PARAMETER_IRODS_COMMITISH +
                                ' --icommands_repo ' + PARAMETER_IRODS_ICOMMANDS_REPO +
                                ' --icommands_commitish ' + PARAMETER_IRODS_ICOMMANDS_COMMITISH +
                                ' -o ' + output_directory
                def branchName = os.split(':')[0]
                parallelBranches["${branchName}"] = {
                    sh build_cmd 
                }
             }
             if (parallelBranches.size() != 0) {
                 parallel(parallelBranches)
             }
         }
     }
}
