def mysh(script) { sh "#!/bin/bash -e\n" + script }
node {
     def build_id = env.BUILD_ID
     def output_directory = env.JENKINS_OUTPUT + '/' + env.JOB_NAME
     def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
     print(dockerfile_dir)
     dir(dockerfile_dir) {
         stage('iRODS Plugin Build') {
             def parallelBranches = [:]
             for(int i = 0; i < 1; i++) {
                def local_repo = PARAMETER_LOCAL_REPO_PREFIX +'__'+ env.JOB_NAME +'__'+ env.BUILD_NUMBER
                def build_cmd = 'python3 -u docker_compose_CI_with_client.py ' +
                                ' --remote_repo=' + PARAMETER_REMOTE_REPO  +
                                ' --local_repo='  + local_repo +
                                ' --commitish=' + PARAMETER_COMMITISH +
                                " --json_config='" + CONFIG_JSON + "'"
                def branchName = 'NoBranch'
                def echo_json = "echo '" + CONFIG_JSON + "'"
                parallelBranches["${branchName}"] = {
                   sh build_cmd
                  //  mysh echo_json
                    //mysh ("echo good-bye----- | tee >(tr a-z A-Z >/tmp/stuff.\$\$) | cat >&2")

                }
             }
             if (parallelBranches.size() != 0) {
                 parallel(parallelBranches)
             }
         }
     }
}
