definition.script:
node {
    def job_number = env.BUILD_ID
    def buildOutputDirectory = env.JENKINS_OUTPUT + '/build_irods_on_base/' + job_number
    def parallelTests = [:]
    stage("Build Base OS") {
        b = build job: "build_base_os",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_PARENT_JOB_ID', value: job_number]
            ]
    }
    stage("iRODS Build") {
        build job: "build_irods_on_base",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_PARENT_JOB_ID', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_REPO', value: PARAMETER_IRODS_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_COMMITISH', value: PARAMETER_IRODS_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_ICOMMANDS_REPO', value: PARAMETER_IRODS_ICOMMANDS_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_ICOMMANDS_COMMITISH', value: PARAMETER_IRODS_ICOMMANDS_COMMITISH]
            ]
    }
    parallelTests["Install and Run iRODS Core Tests"] = {
        stage("Install and Run iRODS Core Tests") {
            build job: "run_irods_tests", 
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_PARENT_JOB_ID', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_REPO', value: PARAMETER_IRODS_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_COMMITISH', value: PARAMETER_IRODS_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_TEST_PARALLELISM', value: PARAMETER_TEST_PARALLELISM]
            ]
        }    
    }
    if(PARAMETER_RUN_PLUGIN_TESTS == "true") {
        parallelTests["Install and Run Plugin Tests"] = {
            stage("Install and Run Plugin Tests") {
                echo "Stage2 Install and Run Plugin Tests"
                build job:"all_plugins_build_and_test_workflow",
                parameters: [
                    [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                    [$class: 'StringParameterValue', name: 'PARAMETER_PARENT_JOB_ID', value: job_number],
                    [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                    [$class: 'StringParameterValue', name: 'PARAMETER_PLUGIN_GIT_COMMITISH', value: PARAMETER_IRODS_COMMITISH],
                    [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE]
                ]
            }  
        }
    }
    if(PARAMETER_RUN_TOPOLOGY_TESTS == "true") {
        parallelTests["Install and Run Topology Tests"] = {
            stage("Install and Run Topology Tests") {
                build job:"run_tests_on_topology",
                parameters: [
                    [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS],
                    [$class: 'StringParameterValue', name: 'PARAMETER_PARENT_JOB_ID', value: job_number],
                    [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                    [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                    [$class: 'StringParameterValue', name: 'PARAMETER_SPECIFIC_TEST', value: 'test_resource_types.Test_Resource_Replication.test_open_write_close_for_repl__3909']
                ]
            }
        }
    }
    if(PARAMETER_RUN_FEDERATION_TESTS == "true") {
        parallelTests["Install and Run Federation Tests"] = {
            stage("Install and Run Federation Tests") {
                build job:"run_tests_on_federation",
                parameters: [
                    [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS],
                    [$class: 'StringParameterValue', name: 'PARAMETER_PARENT_JOB_ID', value: job_number],
                    [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                    [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                    [$class: 'StringParameterValue', name: 'PARAMETER_SPECIFIC_TEST', value: 'test_federation.Test_ICommands.test_ils_l']
                ]
            }
        }
    }
    stage('Install and Run All Tests') {                                                                           
        parallel(parallelTests)
    }
}
