definition.script:
node {
     def build_id = env.BUILD_ID
     if(!PARAMETER_PARENT_JOB_ID.equals('')) {
         build_id = PARAMETER_PARENT_JOB_ID
     }
     print(build_id)
     def output_directory = env.JENKINS_OUTPUT + '/' + env.JOB_NAME + '/' + build_id
     def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
     dir(dockerfile_dir) {
         stage('Install And Run Tests') {
             def base_os = PARAMETER_PLATFORM_TARGETS.split(',')
             def parallelBranches = [:]
             for(int i = 0; i < base_os.size(); i++) {
                def os = base_os[i]
                def run_cmd = 'python run_tests.py -p ' + os + ' -b ' + build_id + ' --test_name_prefix ${BUILD_TAG} --irods_build_dir ' + PARAMETER_IRODS_BUILD_DIR +' -o ' + output_directory + ' --database_type ' + PARAMETER_DATABASE_TYPE + ' --irods_repo ' + PARAMETER_IRODS_REPO + ' --irods_commitish ' + PARAMETER_IRODS_COMMITISH
                parallelBranches["${os}"] = {
                    sh run_cmd 
                }
             }
             if (parallelBranches.size() != 0) {
                 parallel(parallelBranches)
             }
         }
     }
}
