definition.script:
node {
    def job_number = env.BUILD_ID
    def buildOutputDirectory = env.JENKINS_OUTPUT + '/build_irods_on_base/' + job_number
    def parallelTests = [:]
    stage("Build Base OS") {
        b = build job: "build_base_os",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number]
            ]
    }
    stage("iRODS Build") {
        build job: "build_irods_on_base",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_REPO', value: PARAMETER_IRODS_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_COMMITISH', value: PARAMETER_IRODS_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_ICOMMANDS_REPO', value: PARAMETER_IRODS_ICOMMANDS_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_ICOMMANDS_COMMITISH', value: PARAMETER_IRODS_ICOMMANDS_COMMITISH]
            ]
    }
    if(PARAMETER_RUN_CORE_TIMING_TESTS == "true") {
        stage("Install and Run iRODS Core Timing Tests") {
            build job: "run_irods_tests", 
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_REPO', value: PARAMETER_IRODS_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_COMMITISH', value: PARAMETER_IRODS_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_TEST_PARALLELISM', value: PARAMETER_TEST_PARALLELISM],
                [$class: 'StringParameterValue', name: 'PARAMETER_EXTERNALS_ROOT_DIR', value: PARAMETER_EXTERNALS_ROOT_DIR],
                [$class: 'BooleanParameterValue', name: 'PARAMETER_RUN_CORE_TIMING_TESTS', value: PARAMETER_RUN_CORE_TIMING_TESTS]
            ]
        }
    }
    if(PARAMETER_RUN_CORE_TESTS == "true") {
        stage("Install and Run iRODS Core Tests") {
            build job: "run_irods_tests", 
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_REPO', value: PARAMETER_IRODS_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_COMMITISH', value: PARAMETER_IRODS_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_TEST_PARALLELISM', value: PARAMETER_TEST_PARALLELISM],
                [$class: 'StringParameterValue', name: 'PARAMETER_EXTERNALS_ROOT_DIR', value: PARAMETER_EXTERNALS_ROOT_DIR],
                [$class: 'BooleanParameterValue', name: 'PARAMETER_RUN_UNIT_TESTS', value: PARAMETER_RUN_CORE_UNIT_TESTS]
            ]
        }    
    }
    if(PARAMETER_RUN_FEDERATION_TESTS == "true") {
        stage("Install and Run Federation Tests") {
            build job:"run_tests_on_federation",
            parameters: [
                    [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS],
                    [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                    [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                    [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE]
            ]
        }
    }
    if(PARAMETER_RUN_PLUGIN_TESTS == "true") {
        stage("Install and Run Plugin Tests") {
            echo "Stage2 Install and Run Plugin Tests"
            build job:"serialized_all_plugins_build_and_test_workflow",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_PLUGIN_GIT_COMMITISH', value: PARAMETER_IRODS_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE]
            ]
        }  
    }
    if(PARAMETER_RUN_TOPOLOGY_ICAT_TESTS == "true") {
        stage("Install and Run Topology Tests on Provider") {
            build job:"run_tests_on_topology",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS],
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                [$class: 'StringParameterValue', name: 'PARAMETER_TEST_TYPE', value: 'topology_icat']
            ]
        }
    }
    if(PARAMETER_RUN_TOPOLOGY_ICAT_TESTS == "true" && PARAMETER_ENABLE_SSL == "true") {
        stage("Install and Run Topology Tests With SSL on Provider") {
            build job:"run_tests_on_topology",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS],
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                [$class: 'StringParameterValue', name: 'PARAMETER_TEST_TYPE', value: 'topology_icat'],
                [$class: 'BooleanParameterValue', name: 'PARAMETER_ENABLE_SSL', value: PARAMETER_ENABLE_SSL]
            ]
        }
    }
    if(PARAMETER_RUN_TOPOLOGY_RESOURCE_TESTS == "true") {
        stage("Install and Run Topology Tests on Consumer") {
            build job:"run_tests_on_topology",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS],
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                [$class: 'StringParameterValue', name: 'PARAMETER_TEST_TYPE', value: 'topology_resource']
            ]
        }
    }
    if(PARAMETER_RUN_TOPOLOGY_RESOURCE_TESTS == "true" && PARAMETER_ENABLE_SSL == "true") {
        stage("Install and Run Topology Tests With SSL on Consumer") {
            build job:"run_tests_on_topology",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS],
                [$class: 'StringParameterValue', name: 'PARAMETER_IMAGE_TAG', value: job_number],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE],
                [$class: 'StringParameterValue', name: 'PARAMETER_TEST_TYPE', value: 'topology_resource'],
                [$class: 'BooleanParameterValue', name: 'PARAMETER_ENABLE_SSL', value: PARAMETER_ENABLE_SSL]
            ]
        }
    }
}
