definition.script:
node {
     def build_id = env.BUILD_ID
     if(!PARAMETER_PARENT_JOB_ID.equals('')) {
         build_id = PARAMETER_PARENT_JOB_ID
     }
    def buildOutputDirectory = env.JENKINS_OUTPUT + '/build_plugins_on_base/' + build_id
    stage("Build Plugins on Base OS") {
        b = build job: "build_plugins_on_base",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_PARENT_JOB_ID', value: build_id],
                [$class: 'StringParameterValue', name: 'PARAMETER_PLUGIN_GIT_REPO', value: PARAMETER_PLUGIN_GIT_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_PLUGIN_GIT_COMMITISH', value: PARAMETER_PLUGIN_GIT_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: PARAMETER_IRODS_BUILD_DIR]
            ]
        print(buildOutputDirectory)
    }
    stage('Install and Run Plugin Tests') {    
        print(buildOutputDirectory)
        b = build job: "run_plugin_tests",
            parameters: [
                [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGETS', value: PARAMETER_PLATFORM_TARGETS], 
                [$class: 'StringParameterValue', name: 'PARAMETER_PARENT_JOB_ID', value: build_id],
                [$class: 'StringParameterValue', name: 'PARAMETER_PLUGIN_GIT_REPO', value: PARAMETER_PLUGIN_GIT_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_PLUGIN_GIT_COMMITISH', value: PARAMETER_PLUGIN_GIT_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_BUILD_DIR', value: PARAMETER_IRODS_BUILD_DIR],
                [$class: 'StringParameterValue', name: 'PARAMETER_PLUGIN_BUILD_DIR', value: buildOutputDirectory],
                [$class: 'StringParameterValue', name: 'PARAMETER_DATABASE_TYPE', value: PARAMETER_DATABASE_TYPE]
            ]
    }
}
